import React, { useState } from 'react';
import ChatMessage from './components/ChatMessage';
import ChatInput from './components/ChatInput';
import { ChatMessage as ChatMessageType, ChatRequest, ChatResponse } from './types';

const App: React.FC = () => {
    const [messages, setMessages] = useState<ChatMessageType[]>([
        {
            role: 'assistant',
            content: 'Hello! I\'m your Ireland travel assistant. How can I help you today?'
        }
    ]);
    const [isLoading, setIsLoading] = useState(false);

    const handleSendMessage = async (content: string) => {
        try {
            setIsLoading(true);
            
            // Add user message to chat
            const userMessage: ChatMessageType = { role: 'user', content };
            setMessages(prev => [...prev, userMessage]);

            // Prepare the chat request
            const chatRequest: ChatRequest = {
                message: content,
                conversation_history: messages.map(msg => ({
                    role: msg.role,
                    content: msg.content
                }))
            };

            console.log('Preparing to send request to API:', {
                url: '/api/chat',
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: chatRequest
            });

            // Send request to API
            const response = await fetch('/api/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify(chatRequest)
            });

            console.log('Response received:', {
                status: response.status,
                statusText: response.statusText,
                headers: Object.fromEntries(response.headers.entries())
            });

            if (!response.ok) {
                const errorText = await response.text();
                console.error('Error response details:', {
                    status: response.status,
                    statusText: response.statusText,
                    headers: Object.fromEntries(response.headers.entries()),
                    body: errorText
                });
                
                let errorMessage = 'Failed to get response from API';
                try {
                    const errorData = JSON.parse(errorText);
                    errorMessage = errorData.detail || errorMessage;
                } catch (parseError) {
                    console.error('Failed to parse error response:', parseError);
                    errorMessage = `${errorMessage}: ${errorText}`;
                }
                throw new Error(errorMessage);
            }

            const data: ChatResponse = await response.json();
            console.log('Parsed response data:', data);
            
            // Add assistant's response to chat
            const assistantMessage: ChatMessageType = {
                role: 'assistant',
                content: data.response
            };
            setMessages(prev => [...prev, assistantMessage]);

        } catch (error) {
            console.error('Error in handleSendMessage:', error);
            // Add error message to chat
            const errorMessage: ChatMessageType = {
                role: 'assistant',
                content: error instanceof Error ? error.message : 'Sorry, I encountered an error. Please try again.'
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="flex flex-col h-screen bg-gray-100">
            <header className="bg-blue-600 text-white p-4">
                <h1 className="text-2xl font-bold">Ireland Travel Assistant</h1>
            </header>
            
            <main className="flex-1 overflow-y-auto p-4">
                <div className="max-w-3xl mx-auto space-y-4">
                    {messages.map((message, index) => (
                        <ChatMessage key={index} message={message} />
                    ))}
                </div>
            </main>

            <footer className="max-w-3xl mx-auto w-full p-4">
                <ChatInput 
                    onSendMessage={handleSendMessage}
                    isLoading={isLoading}
                />
            </footer>
        </div>
    );
};

export default App;
