import React, { useState, KeyboardEvent } from 'react';
import { ChatInputProps } from '../types';
import { PaperAirplaneIcon } from '@heroicons/react/24/solid';

const ChatInput: React.FC<ChatInputProps> = ({ onSendMessage, isTyping }) => {
  const [message, setMessage] = useState('');

  const handleSubmit = () => {
    if (message.trim() && !isTyping) {
      onSendMessage(message.trim());
      setMessage('');
    }
  };

  const handleKeyPress = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  return (
    <div className="flex items-end space-x-2 bg-white rounded-lg shadow-md p-4">
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Ask about traveling to Ireland..."
        className="flex-1 resize-none rounded-lg border-2 border-gray-300 p-2 focus:border-green-500 focus:outline-none"
        rows={1}
        style={{
          minHeight: '2.5rem',
          maxHeight: '150px',
        }}
        disabled={isTyping}
      />
      <button
        onClick={handleSubmit}
        disabled={!message.trim() || isTyping}
        className={`p-2 rounded-lg ${
          !message.trim() || isTyping
            ? 'bg-gray-300 cursor-not-allowed'
            : 'bg-green-600 hover:bg-green-700'
        }`}
      >
        <PaperAirplaneIcon className="h-5 w-5 text-white" />
      </button>
    </div>
  );
};

export default ChatInput;
